package newpack1;

public class Operations {
	static void add() {
		int a = 10;
		int b = 20;
		int total = a + b;
		System.out.println(total);
	}

	static void sub() {
		int a = 120;
		int b = 50;
		int total = a - b;
		System.out.println(total);
	}

	static void mul() {
		int a = 10;
		int b = 20;
		int total = a * b;
		System.out.println(total);
	}

	static void div() {
		int a = 10;
		int b = 20;
		int total = a * b;
		System.out.println(total);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		add();
		sub();
		mul();
		div();
	}
}
