package newpack1;

public class Switch_Block {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	switch(2)
	{
	case 1: System.out.println("adding two digit");
	break;
	case 2: System.out.println("subracting two digit");
	
	case 3: System.out.println("multiplying two digit");
	break;
	case 4: System.out.println("dividing two digit");
	break;
	default:System.out.println("any");
	}
	}

}
