package newpack1;

public class TypeCasting_Int_to_double {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("implicitly converting......");
	double d1  = 2340;
	System.out.println(d1); 
	System.out.println("Explicitly converting.....");
	double d2 = (double)11345;
	System.out.println(d2);
	int a =55;
	double d3 = (double)a;
	System.out.println(d3);
	}

}
